document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll("[alke-animation]").forEach((el=>{const animationType=el.getAttribute("alke-animation")||"fade-in",blurAmount=el.getAttribute("alke-blur"),type=el.getAttribute("alke-type")||"standard",staggerTime=parseFloat(el.getAttribute("alke-stagger"))||.1,distance=el.getAttribute("alke-distance")||"20%",duration=parseFloat(el.getAttribute("alke-duration"))||1,delay=parseFloat(el.getAttribute("alke-delay"))||0,infinite=el.hasAttribute("alke-infinite"),startTrigger=el.getAttribute("alke-start")||"top 100%";let animationConfig={opacity:1,x:0,y:0,filter:blurAmount?"blur(0px)":"none",duration:duration,delay:delay,ease:"power4.out"};gsap.set("stagger"===type?el.children:el,{opacity:0,x:animationType.includes("left")?`-${distance}`:animationType.includes("right")?distance:0,y:animationType.includes("up")?distance:animationType.includes("down")?`-${distance}`:0,filter:blurAmount?`blur(${blurAmount})`:"none"});const triggerAnimation=()=>{"stagger"===type&&el.children.length>0?gsap.to([...el.children],{...animationConfig,stagger:staggerTime}):gsap.to(el,animationConfig)};gsap.timeline({id:el,scrollTrigger:{trigger:el,start:startTrigger,onEnter:triggerAnimation,onLeaveBack:()=>{infinite&&gsap.set("stagger"===type?el.children:el,{opacity:0,x:animationType.includes("left")?`-${distance}`:animationType.includes("right")?distance:0,y:animationType.includes("up")?distance:animationType.includes("down")?`-${distance}`:0,filter:blurAmount?`blur(${blurAmount})`:"none"})},onEnterBack:triggerAnimation,toggleActions:"play none none none",once:!infinite}})}))}));